import React from 'react';
import { Autoplay, Pagination } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import AvatarCard from './SponsorCard';

const committeeMembers = [
  {
    "Sr.No.": 1,
    "Responsibility": "General Chair",
    "Name_of_Faculty": "Anna Lisa Gentile",
    "Institution": "IBM Research",
    "Country": "US",
    "Image": "./OC_photos/gentile_annalisa.jpeg"
  },
  {
    "Sr.No.": 2,
    "Responsibility": "Local Chair",
    "Name_of_Faculty": "Kouji Kozaki",
    "Institution": "Osaka Electro-Communication University",
    "Country": "Japan",
    "Image": "./OC_photos/Kouji_Kozaki.png"
  },
  // Add the rest of the committee members here
];

// Main component to render a Swiper for each role
const CommitteeSection = () => {
  // Group members by responsibility (role)
  const groupedMembers = committeeMembers.reduce((acc, member) => {
    if (!acc[member.Responsibility]) {
      acc[member.Responsibility] = [];
    }
    acc[member.Responsibility].push(member);
    return acc;
  }, {});

  return (
    <div className="container py-5">
      {Object.keys(groupedMembers).map((role) => (
        <div className="mb-5" key={role}>
          <h2 className="h4 text-primary mb-3">{role}</h2>
          <Swiper
            slidesPerView={3}
            spaceBetween={30}
            autoplay={{
              delay: 3000,
              disableOnInteraction: false,
            }}
            breakpoints={{
              300: {
                slidesPerView: 1,
                spaceBetween: 10,
              },
              500: {
                slidesPerView: 2,
                spaceBetween: 30,
              },
              768: {
                slidesPerView: 3,
                spaceBetween: 30,
              },
              1024: {
                slidesPerView: 4,
                spaceBetween: 40,
              },
            }}
            pagination={{
              clickable: true,
            }}
            modules={[Pagination, Autoplay]}
            className="mySwiper"
          >
            {groupedMembers[role].map((member) => (
              <SwiperSlide key={member["Sr.No."]}>
                <AvatarCard
                  img={member.Image}
                  name={member.Name_of_Faculty}
                  role={member.Responsibility}
                  detail={`${member.Institution}, ${member.Country}`}
                />
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      ))}
    </div>
  );
};

export default CommitteeSection;
