import React from 'react';
// Import Swiper React components
import { Autoplay, Pagination } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
// Import Swiper styles
import 'swiper/css';
import 'swiper/css/pagination';
// Import required modules
import AvatarCard from './SponsorCard';

function SliderSponsor({ users }) {
  return (
    <div className="container text-center">
      {Object.keys(users).map((role, idx) => (
        <div className="mb-5" key={role}>
          <h2 className="display-5 text-primary mb-4">{role}</h2>
          <Swiper
            slidesPerView={3}
            spaceBetween={30}
            autoplay={{
              delay: 3000,
              disableOnInteraction: false,
            }}
            breakpoints={{
              300: { slidesPerView: 1, spaceBetween: 10 },
              500: { slidesPerView: 2, spaceBetween: 30 },
              768: { slidesPerView: 3, spaceBetween: 30 },
              1024: { slidesPerView: 4, spaceBetween: 40 },
            }}
            pagination={{ clickable: true }}
            modules={[Pagination, Autoplay]}
            className="mySwiper d-flex justify-content-center"
          >
            {users[role].map((user) => (
              <SwiperSlide key={user.name} className="d-flex justify-content-center">
                <AvatarCard
                  img={user.img}
                  name={user.name}
                  detail={`${user.institution}, ${user.country}`}
                  className="p-3"
                />
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      ))}
    </div>
  );
}

export default SliderSponsor;
